import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertPlayerSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Get player by ID or username
  app.get("/api/players/:identifier", async (req, res) => {
    try {
      const { identifier } = req.params;

      // Try to get by ID first, then by username
      let player = await storage.getPlayer(identifier);
      if (!player) {
        player = await storage.getPlayerByUsername(identifier);
      }

      if (!player) {
        return res.status(404).json({ message: "Player not found" });
      }

      res.json(player);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Create new player
  app.post("/api/players", async (req, res) => {
    try {
      const validatedData = insertPlayerSchema.parse(req.body);

      // Check if username already exists
      const existingPlayer = await storage.getPlayerByUsername(validatedData.username);
      if (existingPlayer) {
        return res.status(400).json({ message: "Username already exists" });
      }

      const player = await storage.createPlayer(validatedData);
      res.status(201).json(player);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid input", errors: error.errors });
      }
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Update player stats
  app.patch("/api/players/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;

      const player = await storage.updatePlayer(id, updates);
      if (!player) {
        return res.status(404).json({ message: "Player not found" });
      }

      res.json(player);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get leaderboard
  app.get("/api/leaderboard", async (req, res) => {
    try {
      const limit = parseInt(req.query.limit as string) || 10;
      const players = await storage.getTopPlayers(limit);
      res.json(players);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get all upgrades
  app.get("/api/upgrades", async (req, res) => {
    try {
      const upgrades = await storage.getAllUpgrades();
      res.json(upgrades);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get player upgrades
  app.get("/api/players/:id/upgrades", async (req, res) => {
    try {
      const { id } = req.params;
      const playerUpgrades = await storage.getPlayerUpgrades(id);
      res.json(playerUpgrades);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Buy upgrade
  app.post("/api/players/:id/upgrades", async (req, res) => {
    try {
      const { id } = req.params;
      const { upgradeId, quantity = 1 } = req.body;

      const player = await storage.getPlayer(id);
      const upgrade = await storage.getUpgrade(upgradeId);

      if (!player || !upgrade) {
        return res.status(404).json({ message: "Player or upgrade not found" });
      }

      // Calculate cost
      const playerUpgrades = await storage.getPlayerUpgrades(id);
      const existingUpgrade = playerUpgrades.find(pu => pu.upgradeId === upgradeId);
      const currentQuantity = existingUpgrade?.quantity || 0;

      let totalCost = 0;
      for (let i = 0; i < quantity; i++) {
        const multiplier = Math.pow(upgrade.costMultiplier / 100, currentQuantity + i);
        totalCost += Math.floor(upgrade.baseCost * multiplier);
      }

      if (player.totalKush < totalCost) {
        return res.status(400).json({ message: "Insufficient KUSH" });
      }

      // Update player stats
      const updatedPlayer = await storage.updatePlayer(id, {
        totalKush: player.totalKush - totalCost,
        perClickMultiplier: player.perClickMultiplier + (upgrade.clickPowerIncrease * quantity),
        autoIncomePerHour: player.autoIncomePerHour + (upgrade.autoIncomeIncrease * quantity)
      });

      // Add upgrade
      if (existingUpgrade) {
        await storage.buyUpgrade({
          playerId: id,
          upgradeId,
          quantity: currentQuantity + quantity
        });
      } else {
        await storage.buyUpgrade({
          playerId: id,
          upgradeId,
          quantity
        });
      }

      res.json({ player: updatedPlayer, cost: totalCost });
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get all achievements
  app.get("/api/achievements", async (req, res) => {
    try {
      const achievements = await storage.getAllAchievements();
      res.json(achievements);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Get player achievements
  app.get("/api/players/:id/achievements", async (req, res) => {
    try {
      const { id } = req.params;
      const playerAchievements = await storage.getPlayerAchievements(id);
      const achievements = await storage.getAllAchievements();

      const achievementsWithProgress = achievements.map(achievement => {
        const playerAchievement = playerAchievements.find(pa => pa.achievementId === achievement.id);
        return {
          ...achievement,
          progress: playerAchievement?.progress || 0,
          completed: playerAchievement?.completed || false,
          completedAt: playerAchievement?.completedAt
        };
      });

      res.json(achievementsWithProgress);
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // Process click action
  app.post("/api/players/:id/click", async (req, res) => {
    try {
      const { id } = req.params;
      const player = await storage.getPlayer(id);

      if (!player) {
        return res.status(404).json({ message: "Player not found" });
      }

      const kushGained = player.perClickMultiplier;
      const updatedPlayer = await storage.updatePlayer(id, {
        totalKush: player.totalKush + kushGained,
        totalClicks: player.totalClicks + 1
      });

      // Check achievements
      const playerAchievements = await storage.getPlayerAchievements(id);
      const achievements = await storage.getAllAchievements();

      for (const achievement of achievements) {
        const playerAchievement = playerAchievements.find(pa => pa.achievementId === achievement.id);
        if (!playerAchievement?.completed) {
          let progress = 0;
          switch (achievement.requirementType) {
            case 'total_clicks':
              progress = updatedPlayer!.totalClicks;
              break;
            case 'total_kush':
              progress = updatedPlayer!.totalKush;
              break;
          }

          if (progress !== playerAchievement?.progress) {
            await storage.updatePlayerAchievement(id, achievement.id, progress);
          }
        }
      }

      res.json({ 
        player: updatedPlayer, 
        kushGained,
        totalKush: updatedPlayer!.totalKush 
      });
    } catch (error) {
      res.status(500).json({ message: "Internal server error" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type Player, 
  type InsertPlayer,
  type Upgrade,
  type InsertUpgrade,
  type PlayerUpgrade,
  type InsertPlayerUpgrade,
  type Achievement,
  type InsertAchievement,
  type PlayerAchievement,
  type InsertPlayerAchievement
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Player operations
  getPlayer(id: string): Promise<Player | undefined>;
  getPlayerByUsername(username: string): Promise<Player | undefined>;
  createPlayer(player: InsertPlayer): Promise<Player>;
  updatePlayer(id: string, updates: Partial<Player>): Promise<Player | undefined>;
  getTopPlayers(limit?: number): Promise<Player[]>;

  // Upgrade operations
  getAllUpgrades(): Promise<Upgrade[]>;
  getUpgrade(id: string): Promise<Upgrade | undefined>;
  createUpgrade(upgrade: InsertUpgrade): Promise<Upgrade>;

  // Player upgrade operations
  getPlayerUpgrades(playerId: string): Promise<PlayerUpgrade[]>;
  buyUpgrade(playerUpgrade: InsertPlayerUpgrade): Promise<PlayerUpgrade>;

  // Achievement operations
  getAllAchievements(): Promise<Achievement[]>;
  getPlayerAchievements(playerId: string): Promise<PlayerAchievement[]>;
  updatePlayerAchievement(playerId: string, achievementId: string, progress: number): Promise<PlayerAchievement>;
}

export class MemStorage implements IStorage {
  private players: Map<string, Player>;
  private upgrades: Map<string, Upgrade>;
  private playerUpgrades: Map<string, PlayerUpgrade>;
  private achievements: Map<string, Achievement>;
  private playerAchievements: Map<string, PlayerAchievement>;

  constructor() {
    this.players = new Map();
    this.upgrades = new Map();
    this.playerUpgrades = new Map();
    this.achievements = new Map();
    this.playerAchievements = new Map();

    this.initializeGameData();
  }

  private initializeGameData() {
    // Initialize default upgrades
    const defaultUpgrades: InsertUpgrade[] = [
      {
        name: "Better Fingers",
        description: "+1 Kush per click",
        baseCost: 15,
        costMultiplier: 150,
        clickPowerIncrease: 1,
        autoIncomeIncrease: 0,
        icon: "fas fa-hand-pointer",
        category: "click",
        unlockRequirement: 0
      },
      {
        name: "Auto Clicker",
        description: "+0.5 Kush per second",
        baseCost: 100,
        costMultiplier: 150,
        clickPowerIncrease: 0,
        autoIncomeIncrease: 1800, // 0.5 per second = 1800 per hour
        icon: "fas fa-mouse-pointer",
        category: "auto",
        unlockRequirement: 50
      },
      {
        name: "Lucky Fingers",
        description: "+2 Kush per click",
        baseCost: 500,
        costMultiplier: 150,
        clickPowerIncrease: 2,
        autoIncomeIncrease: 0,
        icon: "fas fa-magic",
        category: "click",
        unlockRequirement: 200
      },
      {
        name: "Golden Touch",
        description: "+5 Kush per click",
        baseCost: 2000,
        costMultiplier: 150,
        clickPowerIncrease: 5,
        autoIncomeIncrease: 0,
        icon: "fas fa-gem",
        category: "special",
        unlockRequirement: 1000
      },
      {
        name: "Kush Farm",
        description: "+5 Kush per second",
        baseCost: 5000,
        costMultiplier: 150,
        clickPowerIncrease: 0,
        autoIncomeIncrease: 18000, // 5 per second = 18000 per hour
        icon: "fas fa-seedling",
        category: "auto",
        unlockRequirement: 2500
      }
    ];

    defaultUpgrades.forEach(upgrade => {
      const id = randomUUID();
      this.upgrades.set(id, { 
        ...upgrade, 
        id,
        costMultiplier: upgrade.costMultiplier || 150,
        clickPowerIncrease: upgrade.clickPowerIncrease || 0,
        autoIncomeIncrease: upgrade.autoIncomeIncrease || 0,
        unlockRequirement: upgrade.unlockRequirement || 0
      });
    });

    // Initialize default achievements
    const defaultAchievements: InsertAchievement[] = [
      {
        name: "First Steps",
        description: "Click 10 times",
        requirement: 10,
        requirementType: "total_clicks",
        reward: 5,
        icon: "fas fa-baby"
      },
      {
        name: "Collect 5 KUSH",
        description: "Earn your first 5 KUSH",
        requirement: 5,
        requirementType: "total_kush",
        reward: 10,
        icon: "fas fa-cannabis"
      },
      {
        name: "Green Thumb",
        description: "Reach 25 total KUSH",
        requirement: 25,
        requirementType: "total_kush",
        reward: 25,
        icon: "fas fa-thumbs-up"
      },
      {
        name: "Speed Demon",
        description: "Click 250 times",
        requirement: 250,
        requirementType: "total_clicks",
        reward: 50,
        icon: "fas fa-tachometer-alt"
      },
      {
        name: "Kush Collector",
        description: "Collect 1,000 KUSH",
        requirement: 1000,
        requirementType: "total_kush",
        reward: 500,
        icon: "fas fa-coins"
      },
      {
        name: "Big Spender",
        description: "Buy 5 upgrades",
        requirement: 5,
        requirementType: "upgrades_bought",
        reward: 100,
        icon: "fas fa-shopping-cart"
      }
    ];

    defaultAchievements.forEach(achievement => {
      const id = randomUUID();
      this.achievements.set(id, { ...achievement, id });
    });
  }

  async getPlayer(id: string): Promise<Player | undefined> {
    return this.players.get(id);
  }

  async getPlayerByUsername(username: string): Promise<Player | undefined> {
    return Array.from(this.players.values()).find(
      (player) => player.username === username,
    );
  }

  async createPlayer(insertPlayer: InsertPlayer): Promise<Player> {
    const id = randomUUID();
    const player: Player = { 
      ...insertPlayer, 
      id,
      totalKush: insertPlayer.totalKush || 0,
      totalClicks: insertPlayer.totalClicks || 0,
      perClickMultiplier: insertPlayer.perClickMultiplier || 1,
      autoIncomePerHour: insertPlayer.autoIncomePerHour || 0,
      claimableTokens: insertPlayer.claimableTokens || 0,
      walletAddress: insertPlayer.walletAddress || null,
      referredBy: insertPlayer.referredBy || null,
      createdAt: new Date(),
      lastActive: new Date()
    };
    this.players.set(id, player);

    // Initialize player achievements
    for (const [achievementId] of Array.from(this.achievements.keys())) {
      const playerAchievementId = randomUUID();
      this.playerAchievements.set(playerAchievementId, {
        id: playerAchievementId,
        playerId: id,
        achievementId,
        completed: false,
        progress: 0,
        completedAt: null
      });
    }

    return player;
  }

  async updatePlayer(id: string, updates: Partial<Player>): Promise<Player | undefined> {
    const player = this.players.get(id);
    if (!player) return undefined;

    const updatedPlayer = { ...player, ...updates, lastActive: new Date() };
    this.players.set(id, updatedPlayer);
    return updatedPlayer;
  }

  async getTopPlayers(limit: number = 10): Promise<Player[]> {
    return Array.from(this.players.values())
      .sort((a, b) => b.totalKush - a.totalKush)
      .slice(0, limit);
  }

  async getAllUpgrades(): Promise<Upgrade[]> {
    return Array.from(this.upgrades.values());
  }

  async getUpgrade(id: string): Promise<Upgrade | undefined> {
    return this.upgrades.get(id);
  }

  async createUpgrade(upgrade: InsertUpgrade): Promise<Upgrade> {
    const id = randomUUID();
    const newUpgrade: Upgrade = { 
      ...upgrade, 
      id,
      costMultiplier: upgrade.costMultiplier || 150,
      clickPowerIncrease: upgrade.clickPowerIncrease || 0,
      autoIncomeIncrease: upgrade.autoIncomeIncrease || 0,
      unlockRequirement: upgrade.unlockRequirement || 0
    };
    this.upgrades.set(id, newUpgrade);
    return newUpgrade;
  }

  async getPlayerUpgrades(playerId: string): Promise<PlayerUpgrade[]> {
    return Array.from(this.playerUpgrades.values()).filter(
      (pu) => pu.playerId === playerId
    );
  }

  async buyUpgrade(playerUpgrade: InsertPlayerUpgrade): Promise<PlayerUpgrade> {
    const id = randomUUID();
    const newPlayerUpgrade: PlayerUpgrade = { 
      ...playerUpgrade, 
      id,
      quantity: playerUpgrade.quantity || 0,
      purchasedAt: new Date()
    };
    this.playerUpgrades.set(id, newPlayerUpgrade);
    return newPlayerUpgrade;
  }

  async getAllAchievements(): Promise<Achievement[]> {
    return Array.from(this.achievements.values());
  }

  async getPlayerAchievements(playerId: string): Promise<PlayerAchievement[]> {
    return Array.from(this.playerAchievements.values()).filter(
      (pa) => pa.playerId === playerId
    );
  }

  async updatePlayerAchievement(playerId: string, achievementId: string, progress: number): Promise<PlayerAchievement> {
    const playerAchievement = Array.from(this.playerAchievements.values()).find(
      (pa) => pa.playerId === playerId && pa.achievementId === achievementId
    );

    if (!playerAchievement) {
      throw new Error("Player achievement not found");
    }

    const achievement = this.achievements.get(achievementId);
    const completed = achievement ? progress >= achievement.requirement : false;

    const updated: PlayerAchievement = {
      ...playerAchievement,
      progress,
      completed,
      completedAt: completed ? new Date() : null
    };

    this.playerAchievements.set(playerAchievement.id, updated);
    return updated;
  }
}

export const storage = new MemStorage();

